void loadDungeon(const char *filename, struct DungeonEntity dungeon[DUNGEON_HEIGHT][DUNGEON_WIDTH]){
    FILE *file = fopen(filename, "r");

    for(int i = 0; i < DUNGEON_HEIGHT; i++){
        for(int j = 0; j < DUNGEON_WIDTH; j++){
            fscanf(file, "%d%c%c%c%d", &dungeon[i][j].id, &dungeon[i][j].render[0], &dungeon[i][j].render[1], &dungeon[i][j].render[2], &dungeon[i][j].color_id);
        }
    }
    fclose(file);
}

void saveDungeon(const char *filename, struct DungeonEntity dungeon[DUNGEON_HEIGHT][DUNGEON_WIDTH]){
   FILE *file = fopen(filename, "w");

    for(int i = 0; i < DUNGEON_HEIGHT; i++){
        for(int j = 0; j < DUNGEON_WIDTH; j++){
            fprintf(file, "%d%c%c%c%d\n", dungeon[i][j].id, dungeon[i][j].render[0], dungeon[i][j].render[1], dungeon[i][j].render[2], dungeon[i][j].color_id);
        }
        fprintf(file, "\n");
    }
    fclose(file);
}


